public class StrippingZerosTest {
	public static void main(java.lang.String[] argv) {
		java.math.BigDecimal[][] testCases = new java.math.BigDecimal[][]{ new java.math.BigDecimal[]{ new java.math.BigDecimal("1.00000"), new java.math.BigDecimal("1") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1.000"), new java.math.BigDecimal("1") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1"), new java.math.BigDecimal("1") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0.1234"), new java.math.BigDecimal("0.1234") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0.12340"), new java.math.BigDecimal("0.1234") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0.12340000000"), new java.math.BigDecimal("0.1234") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1234.5678"), new java.math.BigDecimal("1234.5678") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1234.56780"), new java.math.BigDecimal("1234.5678") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1234.567800000"), new java.math.BigDecimal("1234.5678") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0"), new java.math.BigDecimal("0") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0e2"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0e-2"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0e42"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("+0e42"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("-0e42"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0e-42"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("+0e-42"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("-0e-42"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0e-2"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0e100"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("0e-100"), java.math.BigDecimal.ZERO }, new java.math.BigDecimal[]{ new java.math.BigDecimal("10"), new java.math.BigDecimal("1e1") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("20"), new java.math.BigDecimal("2e1") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("100"), new java.math.BigDecimal("1e2") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1000000000"), new java.math.BigDecimal("1e9") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("100000000e1"), new java.math.BigDecimal("1e9") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("10000000e2"), new java.math.BigDecimal("1e9") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1000000e3"), new java.math.BigDecimal("1e9") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("100000e4"), new java.math.BigDecimal("1e9") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1.0000000000000000000000000000"), new java.math.BigDecimal("1") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("-1.0000000000000000000000000000"), new java.math.BigDecimal("-1") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1.00000000000000000000000000001"), new java.math.BigDecimal("1.00000000000000000000000000001") }, new java.math.BigDecimal[]{ new java.math.BigDecimal("1000000000000000000000000000000e4"), new java.math.BigDecimal("1e34") } };
		for (int i = 0; i < testCases.length; i++) {
			if (!testCases[i][0].stripTrailingZeros().equals(testCases[i][1])) {
				throw new java.lang.RuntimeException((((("For input " + testCases[i][0].toString()) + " did not received expected result ") + testCases[i][1].toString()) + ",  got ") + testCases[i][0].stripTrailingZeros());
			}
			testCases[i][0] = testCases[i][0].negate();
			testCases[i][1] = testCases[i][1].negate();
			if (!testCases[i][0].stripTrailingZeros().equals(testCases[i][1])) {
				throw new java.lang.RuntimeException((((("For input " + testCases[i][0].toString()) + " did not received expected result ") + testCases[i][1].toString()) + ",  got ") + testCases[i][0].stripTrailingZeros());
			}
		}
	}
}